import React, { useState, useRef, useEffect } from 'react';
import { useMsal } from '@azure/msal-react';
import { apiConfig } from '../authConfig';

const WeatherChat = ({ user }) => {
  const { instance } = useMsal();
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [questionCount, setQuestionCount] = useState(0);
  const messagesEndRef = useRef(null);

  const exampleQuestions = [
    "What's the weather forecast for Seattle? (latitude: 47.6062, longitude: -122.3321)",
    "Are there any weather alerts for Washington state?",
    "Can you get the forecast for New York City? (latitude: 40.7128, longitude: -74.0060)",
    "Get weather alerts for California (state code: CA)",
    "What's the forecast for Miami? (latitude: 25.7617, longitude: -80.1918)"
  ];

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleLogout = () => {
    instance.logoutPopup().catch(e => {
      console.error('Logout failed:', e);
    });
  };
  const callWeatherAgent = async (question) => {
    try {
      // Call Azure OpenAI with MCP server integration
      const response = await fetch(apiConfig.azureOpenAI.endpoint + '/openai/deployments/' + apiConfig.azureOpenAI.deploymentName + '/chat/completions?api-version=' + apiConfig.azureOpenAI.apiVersion, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'api-key': apiConfig.azureOpenAI.apiKey,
        },
        body: JSON.stringify({
          messages: [
            {
              role: "system",
              content: `You are a helpful weather assistant that can provide weather forecasts and alerts for any location. 
              
You have access to weather tools through an MCP server at ${apiConfig.mcpServer.url}.
Available functions:
- get_forecast(latitude, longitude): Get weather forecast for coordinates
- get_alerts(state_code): Get weather alerts for a US state (use 2-letter state codes like 'WA', 'CA', 'NY')

When users ask for weather information:
1. If they mention a city/location, ask for or look up the latitude/longitude coordinates
2. If they ask about alerts, ask for or determine the US state code
3. Use the appropriate weather function to get real data
4. Present the information in a clear, helpful format with emojis

Remember: You need coordinates (lat/long) for forecasts and state codes for alerts.`
            },
            {
              role: "user", 
              content: question
            }
          ],
          max_tokens: 1000,
          temperature: 0.7,
          tools: [
            {
              type: "function",
              function: {
                name: "get_forecast",
                description: "Get weather forecast for a specific location using coordinates",
                parameters: {
                  type: "object",
                  properties: {
                    latitude: {
                      type: "number",
                      description: "Latitude coordinate"
                    },
                    longitude: {
                      type: "number", 
                      description: "Longitude coordinate"
                    }
                  },
                  required: ["latitude", "longitude"]
                }
              }
            },
            {
              type: "function",
              function: {
                name: "get_alerts",
                description: "Get weather alerts for a US state",
                parameters: {
                  type: "object",
                  properties: {
                    state_code: {
                      type: "string",
                      description: "2-letter US state code (e.g., 'CA', 'NY', 'WA')"
                    }
                  },
                  required: ["state_code"]
                }
              }
            }
          ],
          tool_choice: "auto"
        })
      });

      if (!response.ok) {
        throw new Error(`Azure OpenAI API error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      
      // Check if Azure OpenAI wants to call a function
      if (data.choices[0].message.tool_calls) {
        const toolCall = data.choices[0].message.tool_calls[0];
        const functionName = toolCall.function.name;
        const functionArgs = JSON.parse(toolCall.function.arguments);
        
        // Call the MCP server function
        let mcpResponse;
        if (functionName === 'get_forecast') {
          mcpResponse = await callMCPFunction('get_forecast', functionArgs);
        } else if (functionName === 'get_alerts') {
          mcpResponse = await callMCPFunction('get_alerts', functionArgs);
        }
        
        // Send the function result back to Azure OpenAI
        const finalResponse = await fetch(apiConfig.azureOpenAI.endpoint + '/openai/deployments/' + apiConfig.azureOpenAI.deploymentName + '/chat/completions?api-version=' + apiConfig.azureOpenAI.apiVersion, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'api-key': apiConfig.azureOpenAI.apiKey,
          },
          body: JSON.stringify({
            messages: [
              {
                role: "system",
                content: `You are a helpful weather assistant. Present weather data in a clear, helpful format with emojis.`
              },
              {
                role: "user", 
                content: question
              },
              {
                role: "assistant",
                content: null,
                tool_calls: data.choices[0].message.tool_calls
              },
              {
                role: "tool",
                tool_call_id: toolCall.id,
                content: JSON.stringify(mcpResponse)
              }
            ],
            max_tokens: 1000,
            temperature: 0.7
          })
        });
        
        const finalData = await finalResponse.json();
        return finalData.choices[0].message.content;
      }
      
      return data.choices[0].message.content;
      
    } catch (error) {
      console.error('Weather API Error:', error);
      throw new Error(`Failed to get weather information: ${error.message}`);
    }
  };

  const callMCPFunction = async (functionName, args) => {
    try {
      const response = await fetch(apiConfig.mcpServer.url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-api-key': apiConfig.mcpServer.apiKey,
        },
        body: JSON.stringify({
          jsonrpc: "2.0",
          method: "tools/call",
          params: {
            name: functionName,
            arguments: args
          },
          id: Date.now()
        })
      });

      if (!response.ok) {
        throw new Error(`MCP server error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      
      if (data.error) {
        throw new Error(`MCP function error: ${data.error.message}`);
      }
      
      return data.result;
      
    } catch (error) {
      console.error('MCP Server Error:', error);
      throw error;
    }
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage = inputValue.trim();
    setInputValue('');
    setError('');
    setIsLoading(true);

    // Add user message
    const newUserMessage = {
      id: Date.now(),
      type: 'user',
      content: userMessage,
      timestamp: new Date()
    };
    
    setMessages(prev => [...prev, newUserMessage]);
    setQuestionCount(prev => prev + 1);

    try {
      // Simulate API call to your weather agent
      const response = await simulateWeatherResponse(userMessage);
      
      // Add assistant response
      const assistantMessage = {
        id: Date.now() + 1,
        type: 'assistant',
        content: response,
        timestamp: new Date(),
        metadata: {
          mcpServer: 'WeatherTools MCP Server',
          serverUrl: apiConfig.mcpServer.url,
          model: `${apiConfig.agent.name} (${apiConfig.azureOpenAI.deploymentName})`,
          inputWords: userMessage.split(' ').length,
          responseWords: response.split(' ').length
        }
      };
      
      setMessages(prev => [...prev, assistantMessage]);
      
    } catch (err) {
      setError('Failed to get weather information. Please try again.');
      console.error('Weather API error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleExampleClick = (question) => {
    setInputValue(question);
  };

  return (
    <div className="weather-app">
      <div className="app-header">
        <h1>üå§Ô∏è Weather Agent</h1>
        <div className="user-info">
          <span>Welcome, {user?.name || 'User'}!</span>
          <button className="logout-button" onClick={handleLogout}>
            Sign Out
          </button>
        </div>
      </div>
      
      <div className="chat-container">
        <div className="messages-area">
          {messages.length === 0 && (
            <div className="example-questions">
              <h3>üåü Example Questions:</h3>
              <ul>
                {exampleQuestions.map((question, index) => (
                  <li key={index} onClick={() => handleExampleClick(question)}>
                    {question}
                  </li>
                ))}
              </ul>
              <p><strong>üí° Tip:</strong> Click on any example above or type your own weather question!</p>
            </div>
          )}
          
          {error && (
            <div className="error-message">
              ‚ùå {error}
            </div>
          )}
          
          {messages.map((message) => (
            <div key={message.id} className={`message ${message.type}`}>
              <div className="message-label">
                {message.type === 'user' ? 'ü§î You asked:' : 'ü§ñ Weather Agent:'}
              </div>
              <div className="message-content">
                {message.content}
              </div>
              {message.metadata && (
                <div className="info-panel">
                  <h4>üìä Response Details:</h4>
                  <p><strong>MCP Server:</strong> {message.metadata.mcpServer}</p>
                  <p><strong>Model:</strong> {message.metadata.model}</p>
                  <p><strong>Input:</strong> {message.metadata.inputWords} words</p>
                  <p><strong>Response:</strong> {message.metadata.responseWords} words</p>
                  <p><strong>Question #{questionCount}</strong></p>
                </div>
              )}
            </div>
          ))}
          
          {isLoading && (
            <div className="message assistant">
              <div className="message-label">ü§ñ Weather Agent:</div>
              <div className="loading">Getting weather information...</div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
        
        <div className="input-area">
          <div className="input-container">
            <textarea
              className="message-input"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask me about the weather... (e.g., What's the forecast for London?)"
              rows={1}
              disabled={isLoading}
            />
            <button
              className="send-button"
              onClick={handleSendMessage}
              disabled={!inputValue.trim() || isLoading}
            >
              {isLoading ? '‚è≥' : 'üöÄ'} Send
            </button>
          </div>
          <div style={{ marginTop: '10px', fontSize: '0.9rem', color: '#666' }}>
            üí° Connected to: {apiConfig.mcpServer.url} | Questions asked: {questionCount}
          </div>
        </div>
      </div>
    </div>
  );
};

export default WeatherChat;
