import React, { useState, useRef, useEffect } from 'react';
import { useMsal } from '@azure/msal-react';
import { apiConfig } from '../authConfig';

const WeatherChat = ({ user }) => {
  const { instance } = useMsal();
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [questionCount, setQuestionCount] = useState(0);
  const messagesEndRef = useRef(null);

  const exampleQuestions = [
    "What's the weather forecast for Seattle? (latitude: 47.6062, longitude: -122.3321)",
    "Are there any weather alerts for Washington state?",
    "Can you get the forecast for New York City? (latitude: 40.7128, longitude: -74.0060)",
    "Get weather alerts for California (state code: CA)",
    "What's the forecast for Miami? (latitude: 25.7617, longitude: -80.1918)"
  ];

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleLogout = () => {
    instance.logoutPopup().catch(e => {
      console.error('Logout failed:', e);
    });
  };  const callWeatherAgent = async (question) => {
    try {
      console.log('Calling MCP server at:', apiConfig.mcpServer.url);
      console.log('With API key:', apiConfig.mcpServer.apiKey ? 'Present' : 'Missing');
      
      // For now, let's test direct connection to your MCP server
      // Since your MCP server is exposed via SSE, we'll try a direct approach
      
      // Parse the question to extract function calls
      const lowerQuestion = question.toLowerCase();
      let mcpResponse;
      
      if (lowerQuestion.includes('forecast') || lowerQuestion.includes('weather for')) {
        // Try to extract coordinates from common patterns
        const latLongMatch = question.match(/latitude[:\s]*([0-9.-]+).*longitude[:\s]*([0-9.-]+)/i);
        if (latLongMatch) {
          const latitude = parseFloat(latLongMatch[1]);
          const longitude = parseFloat(latLongMatch[2]);
          mcpResponse = await callMCPFunction('get_forecast', { latitude, longitude });
        } else {
          return "To get a weather forecast, I need latitude and longitude coordinates. Please provide them in your question like: 'Get forecast for latitude: 47.6062, longitude: -122.3321'";
        }
      } else if (lowerQuestion.includes('alert')) {
        // Try to extract state code
        const stateMatch = question.match(/\b([A-Z]{2})\b/);
        if (stateMatch) {
          mcpResponse = await callMCPFunction('get_alerts', { state_code: stateMatch[1] });
        } else {
          return "To get weather alerts, I need a US state code (2 letters like 'WA', 'CA', 'NY'). Please include it in your question.";
        }
      } else {
        return `I can help you with weather forecasts and alerts! Try asking:
‚Ä¢ "Get forecast for latitude: 47.6062, longitude: -122.3321" (Seattle)
‚Ä¢ "Get weather alerts for WA" (Washington state)
‚Ä¢ "Show forecast for latitude: 40.7128, longitude: -74.0060" (NYC)`;
      }
      
      // Format the MCP response nicely
      if (mcpResponse && mcpResponse.content) {
        return mcpResponse.content;
      } else if (mcpResponse) {
        return JSON.stringify(mcpResponse, null, 2);
      } else {
        return "No data received from weather service.";
      }
      
    } catch (error) {
      console.error('Weather API Error:', error);
      throw new Error(`Failed to get weather information: ${error.message}`);
    }
  };

  const callMCPFunction = async (functionName, args) => {
    try {
      const response = await fetch(apiConfig.mcpServer.url, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'x-api-key': apiConfig.mcpServer.apiKey,
        },
        body: JSON.stringify({
          jsonrpc: "2.0",
          method: "tools/call",
          params: {
            name: functionName,
            arguments: args
          },
          id: Date.now()
        })
      });

      if (!response.ok) {
        throw new Error(`MCP server error: ${response.status} ${response.statusText}`);
      }

      const data = await response.json();
      
      if (data.error) {
        throw new Error(`MCP function error: ${data.error.message}`);
      }
      
      return data.result;
      
    } catch (error) {
      console.error('MCP Server Error:', error);
      throw error;
    }
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage = inputValue.trim();
    setInputValue('');
    setError('');
    setIsLoading(true);

    // Add user message
    const newUserMessage = {
      id: Date.now(),
      type: 'user',
      content: userMessage,
      timestamp: new Date()
    };
    
    setMessages(prev => [...prev, newUserMessage]);
    setQuestionCount(prev => prev + 1);    try {
      // Call Azure OpenAI with MCP server integration
      const response = await callWeatherAgent(userMessage);
      
      // Add assistant response
      const assistantMessage = {
        id: Date.now() + 1,
        type: 'assistant',
        content: response,
        timestamp: new Date(),
        metadata: {
          mcpServer: 'WeatherTools MCP Server',
          serverUrl: apiConfig.mcpServer.url,
          model: `${apiConfig.agent.name} (${apiConfig.azureOpenAI.deploymentName})`,
          inputWords: userMessage.split(' ').length,
          responseWords: response.split(' ').length
        }
      };
      
      setMessages(prev => [...prev, assistantMessage]);
      
    } catch (err) {
      setError('Failed to get weather information. Please try again.');
      console.error('Weather API error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleExampleClick = (question) => {
    setInputValue(question);
  };

  return (
    <div className="weather-app">
      <div className="app-header">
        <h1>üå§Ô∏è Weather Agent</h1>
        <div className="user-info">
          <span>Welcome, {user?.name || 'User'}!</span>
          <button className="logout-button" onClick={handleLogout}>
            Sign Out
          </button>
        </div>
      </div>
      
      <div className="chat-container">
        <div className="messages-area">
          {messages.length === 0 && (
            <div className="example-questions">
              <h3>üåü Example Questions:</h3>
              <ul>
                {exampleQuestions.map((question, index) => (
                  <li key={index} onClick={() => handleExampleClick(question)}>
                    {question}
                  </li>
                ))}
              </ul>
              <p><strong>üí° Tip:</strong> Click on any example above or type your own weather question!</p>
            </div>
          )}
          
          {error && (
            <div className="error-message">
              ‚ùå {error}
            </div>
          )}
          
          {messages.map((message) => (
            <div key={message.id} className={`message ${message.type}`}>
              <div className="message-label">
                {message.type === 'user' ? 'ü§î You asked:' : 'ü§ñ Weather Agent:'}
              </div>
              <div className="message-content">
                {message.content}
              </div>
              {message.metadata && (
                <div className="info-panel">
                  <h4>üìä Response Details:</h4>
                  <p><strong>MCP Server:</strong> {message.metadata.mcpServer}</p>
                  <p><strong>Model:</strong> {message.metadata.model}</p>
                  <p><strong>Input:</strong> {message.metadata.inputWords} words</p>
                  <p><strong>Response:</strong> {message.metadata.responseWords} words</p>
                  <p><strong>Question #{questionCount}</strong></p>
                </div>
              )}
            </div>
          ))}
          
          {isLoading && (
            <div className="message assistant">
              <div className="message-label">ü§ñ Weather Agent:</div>
              <div className="loading">Getting weather information...</div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
        
        <div className="input-area">
          <div className="input-container">
            <textarea
              className="message-input"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask me about the weather... (e.g., What's the forecast for London?)"
              rows={1}
              disabled={isLoading}
            />
            <button
              className="send-button"
              onClick={handleSendMessage}
              disabled={!inputValue.trim() || isLoading}
            >
              {isLoading ? '‚è≥' : 'üöÄ'} Send
            </button>
          </div>
          <div style={{ marginTop: '10px', fontSize: '0.9rem', color: '#666' }}>
            üí° Connected to: {apiConfig.mcpServer.url} | Questions asked: {questionCount}
          </div>
        </div>
      </div>
    </div>
  );
};

export default WeatherChat;
