import React, { useState, useRef, useEffect } from 'react';
import { useMsal } from '@azure/msal-react';
import { apiConfig } from '../authConfig';

const WeatherChat = ({ user }) => {
  const { instance } = useMsal();
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [questionCount, setQuestionCount] = useState(0);
  const messagesEndRef = useRef(null);

  const exampleQuestions = [
    "What's the weather forecast for Seattle? (latitude: 47.6062, longitude: -122.3321)",
    "Are there any weather alerts for Washington state?",
    "Can you get the forecast for New York City? (latitude: 40.7128, longitude: -74.0060)",
    "Get weather alerts for California (state code: CA)",
    "What's the forecast for Miami? (latitude: 25.7617, longitude: -80.1918)"
  ];

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleLogout = () => {
    instance.logoutPopup().catch(e => {
      console.error('Logout failed:', e);
    });
  };

  const simulateWeatherResponse = async (question) => {
    // Simulate calling the MCP weather server
    // In a real implementation, you would make an actual HTTP request to your Azure OpenAI
    // and MCP server endpoints
    
    const responses = {
      'seattle': `Based on the weather forecast for Seattle (47.6062, -122.3321), here's what to expect:

üå§Ô∏è **Current Weather**: Partly cloudy with temperatures around 62¬∞F (17¬∞C)
üìÖ **Tomorrow**: Mostly sunny with highs reaching 68¬∞F (20¬∞C) and lows around 52¬∞F (11¬∞C)
üåßÔ∏è **Precipitation**: 20% chance of light rain in the evening
üí® **Wind**: Light breeze from the northwest at 8-12 mph
üå°Ô∏è **Humidity**: 65%

Perfect weather for outdoor activities! Don't forget a light jacket for the evening.`,
      
      'washington': `‚ö†Ô∏è **Weather Alerts for Washington State**:

Currently monitoring:
‚Ä¢ **Wind Advisory**: Gusty winds up to 45 mph expected in coastal areas
‚Ä¢ **Flood Watch**: Minor flooding possible in low-lying areas near rivers
‚Ä¢ **Air Quality Alert**: Moderate air quality due to seasonal conditions

Stay informed and take necessary precautions when traveling.`,
      
      'new york': `üèôÔ∏è **Weather Forecast for New York City** (40.7128, -74.0060):

**Current**: Clear skies, 58¬∞F (14¬∞C)
**Tomorrow**: Sunny with increasing clouds, high 72¬∞F (22¬∞C), low 60¬∞F (16¬∞C)
**Weekend Outlook**: Partly cloudy with scattered afternoon showers
**Wind**: Light from the south at 5-10 mph

Great weather for exploring the city!`,
      
      'california': `üö® **Weather Alerts for California** (CA):

Active alerts:
‚Ä¢ **Heat Warning**: Excessive heat in inland valleys (95-105¬∞F)
‚Ä¢ **Red Flag Warning**: High fire danger due to dry conditions and winds
‚Ä¢ **Air Quality Alert**: Unhealthy air quality in several metropolitan areas

Please stay hydrated and limit outdoor activities during peak heat hours.`,
      
      'miami': `üå¥ **Miami Weather Forecast** (25.7617, -80.1918):

**Now**: Partly cloudy, 82¬∞F (28¬∞C), feels like 88¬∞F
**Tomorrow**: Warm and humid with afternoon thunderstorms, high 85¬∞F (29¬∞C)
**Humidity**: 78% (typical for Miami!)
**UV Index**: High (8/10)
**Rain Chance**: 60% in the afternoon

Perfect beach weather in the morning, seek shade during storms!`
    };

    // Simple keyword matching for demo
    const lowerQuestion = question.toLowerCase();
    let response = "I'm unable to retrieve specific weather data at the moment. Please try again later or contact support if the issue persists.";
    
    if (lowerQuestion.includes('seattle')) response = responses.seattle;
    else if (lowerQuestion.includes('washington')) response = responses.washington;
    else if (lowerQuestion.includes('new york')) response = responses['new york'];
    else if (lowerQuestion.includes('california')) response = responses.california;
    else if (lowerQuestion.includes('miami')) response = responses.miami;
    
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 1500));
    
    return response;
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage = inputValue.trim();
    setInputValue('');
    setError('');
    setIsLoading(true);

    // Add user message
    const newUserMessage = {
      id: Date.now(),
      type: 'user',
      content: userMessage,
      timestamp: new Date()
    };
    
    setMessages(prev => [...prev, newUserMessage]);
    setQuestionCount(prev => prev + 1);

    try {
      // Simulate API call to your weather agent
      const response = await simulateWeatherResponse(userMessage);
      
      // Add assistant response
      const assistantMessage = {
        id: Date.now() + 1,
        type: 'assistant',
        content: response,
        timestamp: new Date(),
        metadata: {
          mcpServer: 'WeatherTools MCP Server',
          serverUrl: apiConfig.mcpServer.url,
          model: `${apiConfig.agent.name} (${apiConfig.azureOpenAI.deploymentName})`,
          inputWords: userMessage.split(' ').length,
          responseWords: response.split(' ').length
        }
      };
      
      setMessages(prev => [...prev, assistantMessage]);
      
    } catch (err) {
      setError('Failed to get weather information. Please try again.');
      console.error('Weather API error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleExampleClick = (question) => {
    setInputValue(question);
  };

  return (
    <div className="weather-app">
      <div className="app-header">
        <h1>üå§Ô∏è Weather Agent</h1>
        <div className="user-info">
          <span>Welcome, {user?.name || 'User'}!</span>
          <button className="logout-button" onClick={handleLogout}>
            Sign Out
          </button>
        </div>
      </div>
      
      <div className="chat-container">
        <div className="messages-area">
          {messages.length === 0 && (
            <div className="example-questions">
              <h3>üåü Example Questions:</h3>
              <ul>
                {exampleQuestions.map((question, index) => (
                  <li key={index} onClick={() => handleExampleClick(question)}>
                    {question}
                  </li>
                ))}
              </ul>
              <p><strong>üí° Tip:</strong> Click on any example above or type your own weather question!</p>
            </div>
          )}
          
          {error && (
            <div className="error-message">
              ‚ùå {error}
            </div>
          )}
          
          {messages.map((message) => (
            <div key={message.id} className={`message ${message.type}`}>
              <div className="message-label">
                {message.type === 'user' ? 'ü§î You asked:' : 'ü§ñ Weather Agent:'}
              </div>
              <div className="message-content">
                {message.content}
              </div>
              {message.metadata && (
                <div className="info-panel">
                  <h4>üìä Response Details:</h4>
                  <p><strong>MCP Server:</strong> {message.metadata.mcpServer}</p>
                  <p><strong>Model:</strong> {message.metadata.model}</p>
                  <p><strong>Input:</strong> {message.metadata.inputWords} words</p>
                  <p><strong>Response:</strong> {message.metadata.responseWords} words</p>
                  <p><strong>Question #{questionCount}</strong></p>
                </div>
              )}
            </div>
          ))}
          
          {isLoading && (
            <div className="message assistant">
              <div className="message-label">ü§ñ Weather Agent:</div>
              <div className="loading">Getting weather information...</div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
        
        <div className="input-area">
          <div className="input-container">
            <textarea
              className="message-input"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask me about the weather... (e.g., What's the forecast for London?)"
              rows={1}
              disabled={isLoading}
            />
            <button
              className="send-button"
              onClick={handleSendMessage}
              disabled={!inputValue.trim() || isLoading}
            >
              {isLoading ? '‚è≥' : 'üöÄ'} Send
            </button>
          </div>
          <div style={{ marginTop: '10px', fontSize: '0.9rem', color: '#666' }}>
            üí° Connected to: {apiConfig.mcpServer.url} | Questions asked: {questionCount}
          </div>
        </div>
      </div>
    </div>
  );
};

export default WeatherChat;
