import React, { useState, useRef, useEffect } from 'react';
import { useMsal } from '@azure/msal-react';
import { apiConfig } from '../authConfig';

const WeatherChat = ({ user }) => {
  const { instance } = useMsal();
  const [messages, setMessages] = useState([]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState('');
  const [questionCount, setQuestionCount] = useState(0);
  const messagesEndRef = useRef(null);  const exampleQuestions = [
    "What's the weather forecast for Seattle?",
    "Get weather alerts for Washington",
    "Show me the forecast for New York City", 
    "Get weather alerts for California",
    "What's the weather like in Miami?"
  ];

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleLogout = () => {
    instance.logoutPopup().catch(e => {
      console.error('Logout failed:', e);
    });
  };  const callWeatherAgent = async (question) => {
    try {
      console.log('Calling MCP server at:', apiConfig.mcpServer.url);
      console.log('With API key:', apiConfig.mcpServer.apiKey ? 'Present' : 'Missing');
      
      // For now, let's test direct connection to your MCP server
      // Since your MCP server is exposed via SSE, we'll try a direct approach
      
      // Parse the question to extract function calls
      const lowerQuestion = question.toLowerCase();
      let mcpResponse;      if (lowerQuestion.includes('forecast') || lowerQuestion.includes('weather for') || lowerQuestion.includes('weather like')) {
        // For demo purposes, map common cities to coordinates
        const cityCoords = {
          'seattle': { latitude: 47.6062, longitude: -122.3321 },
          'new york': { latitude: 40.7128, longitude: -74.0060 },
          'new york city': { latitude: 40.7128, longitude: -74.0060 },
          'nyc': { latitude: 40.7128, longitude: -74.0060 },
          'miami': { latitude: 25.7617, longitude: -80.1918 },
          'los angeles': { latitude: 34.0522, longitude: -118.2437 },
          'chicago': { latitude: 41.8781, longitude: -87.6298 }
        };
        
        const locationMatch = question.match(/(?:for|in|at)\s+([a-zA-Z\s,]+)/i);
        if (locationMatch) {
          const location = locationMatch[1].trim().toLowerCase();
          const coords = cityCoords[location];
          if (coords) {
            mcpResponse = await callMCPFunction('get_forecast', coords);
          } else {
            return `Location "${locationMatch[1].trim()}" not found. Available cities: ${Object.keys(cityCoords).join(', ')}`;
          }
        } else {
          return "To get a weather forecast, please specify a location like: 'What's the weather forecast for Seattle?' or 'Show me the forecast for New York City'";
        }
      } else if (lowerQuestion.includes('alert')) {
        // Extract location/state from natural language  
        const locationMatch = question.match(/(?:for|in|at)\s+([a-zA-Z\s,]+)/i);
        if (locationMatch) {
          const location = locationMatch[1].trim();
          mcpResponse = await callMCPFunction('get_alerts', { location });
        } else {
          return "To get weather alerts, please specify a location like: 'Get weather alerts for Washington' or 'Show alerts for California'";
        }
      } else {
        return `I can help you with weather forecasts and alerts! Try asking:
‚Ä¢ "What's the weather forecast for Seattle?"
‚Ä¢ "Get weather alerts for Washington"  
‚Ä¢ "Show me the forecast for New York City"`;
      }
      
      // Format the MCP response nicely
      if (mcpResponse && mcpResponse.content) {
        return mcpResponse.content;
      } else if (mcpResponse) {
        return JSON.stringify(mcpResponse, null, 2);
      } else {
        return "No data received from weather service.";
      }
      
    } catch (error) {
      console.error('Weather API Error:', error);
      throw new Error(`Failed to get weather information: ${error.message}`);
    }
  };  const callMCPFunction = async (functionName, args) => {
    try {
      console.log(`Calling MCP function: ${functionName}`, args);
      
      let endpoint;
      let queryParams = new URLSearchParams();
      
      if (functionName === 'get_forecast') {
        endpoint = '/weather/forecast';
        if (args.latitude && args.longitude) {
          queryParams.append('latitude', args.latitude);
          queryParams.append('longitude', args.longitude);
        } else if (args.location) {
          // For now, return a message that geocoding needs to be implemented
          return {
            content: `Geocoding not yet implemented. Please provide coordinates for ${args.location}.`
          };
        }
      } else if (functionName === 'get_alerts') {
        endpoint = '/weather/alerts';
        if (args.state_code) {
          queryParams.append('state', args.state_code);
        } else if (args.location) {
          // Try to extract state code from location
          const stateCodes = {
            'washington': 'WA', 'wa': 'WA',
            'california': 'CA', 'ca': 'CA', 
            'new york': 'NY', 'ny': 'NY',
            'florida': 'FL', 'fl': 'FL',
            'texas': 'TX', 'tx': 'TX'
          };
          const stateCode = stateCodes[args.location.toLowerCase()];
          if (stateCode) {
            queryParams.append('state', stateCode);
          } else {
            return {
              content: `Could not determine state code for "${args.location}". Please use a US state name or 2-letter code.`
            };
          }
        }
      }
      
      const url = `${apiConfig.mcpServer.url.replace('/sse', '')}${endpoint}?${queryParams}`;
      console.log('Fetching from:', url);
      
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'x-api-key': apiConfig.mcpServer.apiKey,
          'Accept': 'application/json',
          'Cache-Control': 'no-cache',
        },
        mode: 'cors'
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const result = await response.json();
      
      if (result.status === 'success') {
        return {
          content: result.data
        };
      } else {
        return {
          content: `Error: ${result.message || 'Unknown error occurred'}`
        };
      }
      
    } catch (error) {
      console.error('MCP Server Error:', error);
      
      if (error.name === 'TypeError' && error.message.includes('fetch')) {
        return {
          content: `‚ö†Ô∏è Network Error: Cannot connect to MCP server.
          
The MCP server at ${apiConfig.mcpServer.url} may be down or unreachable.

Error details: ${error.message}`
        };
      }
      
      throw error;
    }
  };

  const handleSendMessage = async () => {
    if (!inputValue.trim() || isLoading) return;

    const userMessage = inputValue.trim();
    setInputValue('');
    setError('');
    setIsLoading(true);

    // Add user message
    const newUserMessage = {
      id: Date.now(),
      type: 'user',
      content: userMessage,
      timestamp: new Date()
    };
    
    setMessages(prev => [...prev, newUserMessage]);
    setQuestionCount(prev => prev + 1);    try {
      // Call Azure OpenAI with MCP server integration
      const response = await callWeatherAgent(userMessage);
      
      // Add assistant response
      const assistantMessage = {
        id: Date.now() + 1,
        type: 'assistant',
        content: response,
        timestamp: new Date(),
        metadata: {
          mcpServer: 'WeatherTools MCP Server',
          serverUrl: apiConfig.mcpServer.url,
          model: `${apiConfig.agent.name} (${apiConfig.azureOpenAI.deploymentName})`,
          inputWords: userMessage.split(' ').length,
          responseWords: response.split(' ').length
        }
      };
      
      setMessages(prev => [...prev, assistantMessage]);
      
    } catch (err) {
      setError('Failed to get weather information. Please try again.');
      console.error('Weather API error:', err);
    } finally {
      setIsLoading(false);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSendMessage();
    }
  };

  const handleExampleClick = (question) => {
    setInputValue(question);
  };

  return (
    <div className="weather-app">
      <div className="app-header">
        <h1>üå§Ô∏è Weather Agent</h1>
        <div className="user-info">
          <span>Welcome, {user?.name || 'User'}!</span>
          <button className="logout-button" onClick={handleLogout}>
            Sign Out
          </button>
        </div>
      </div>
      
      <div className="chat-container">
        <div className="messages-area">
          {messages.length === 0 && (
            <div className="example-questions">
              <h3>üåü Example Questions:</h3>
              <ul>
                {exampleQuestions.map((question, index) => (
                  <li key={index} onClick={() => handleExampleClick(question)}>
                    {question}
                  </li>
                ))}
              </ul>
              <p><strong>üí° Tip:</strong> Click on any example above or type your own weather question!</p>
            </div>
          )}
          
          {error && (
            <div className="error-message">
              ‚ùå {error}
            </div>
          )}
          
          {messages.map((message) => (
            <div key={message.id} className={`message ${message.type}`}>
              <div className="message-label">
                {message.type === 'user' ? 'ü§î You asked:' : 'ü§ñ Weather Agent:'}
              </div>
              <div className="message-content">
                {message.content}
              </div>
              {message.metadata && (
                <div className="info-panel">
                  <h4>üìä Response Details:</h4>
                  <p><strong>MCP Server:</strong> {message.metadata.mcpServer}</p>
                  <p><strong>Model:</strong> {message.metadata.model}</p>
                  <p><strong>Input:</strong> {message.metadata.inputWords} words</p>
                  <p><strong>Response:</strong> {message.metadata.responseWords} words</p>
                  <p><strong>Question #{questionCount}</strong></p>
                </div>
              )}
            </div>
          ))}
          
          {isLoading && (
            <div className="message assistant">
              <div className="message-label">ü§ñ Weather Agent:</div>
              <div className="loading">Getting weather information...</div>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
        
        <div className="input-area">
          <div className="input-container">
            <textarea
              className="message-input"
              value={inputValue}
              onChange={(e) => setInputValue(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Ask me about the weather... (e.g., What's the forecast for London?)"
              rows={1}
              disabled={isLoading}
            />
            <button
              className="send-button"
              onClick={handleSendMessage}
              disabled={!inputValue.trim() || isLoading}
            >
              {isLoading ? '‚è≥' : 'üöÄ'} Send
            </button>
          </div>
          <div style={{ marginTop: '10px', fontSize: '0.9rem', color: '#666' }}>
            üí° Connected to: {apiConfig.mcpServer.url} | Questions asked: {questionCount}
          </div>
        </div>
      </div>
    </div>
  );
};

export default WeatherChat;
